import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Square(props) {
  return (
    <button
      className="square"
      onClick={() => props.onClick()}
    >
      {props.value}
    </button>
  );
}

class Board extends React.Component {
  constructor(props) {
    super(props);
    var array = [[null, null, null, null, null, null, null, null,], [null, null, null, null, null, null, null, null,], [null, null, null, null, null, null, null, null,], [null, null, null, null, null, null, null, null,], [null, null, null, null, null, null, null, null,], [null, null, null, null, null, null, null, null,], [null, null, null, null, null, null, null, null,], [null, null, null, null, null, null, null, null,]]
    this.state = {
      squares: array,
      isX: true,
    };
    this.state.squares[3][3] = 'O';
    this.state.squares[3][4] = 'X';
    this.state.squares[4][3] = 'X';
    this.state.squares[4][4] = 'O';
  }
  handleClick(i, j) {
    const squares = this.state.squares.slice();

    if (calculateWinner(squares) || squares[i][j]) {
      return;
    }
    const value = this.state.isX ? 'X' : 'O';
    squares[i][j] = value;
    this.setState({
      squares: squares,
      isX: !this.state.isX
    });
    //ボード全体配列と置かれたマスを渡して、裏返すべきマス配列を受け取る
    alter(squares, i, j);
  }
  renderSquare(i, j) {
    return (
      <Square
        value={this.state.squares[i][j]}
        onClick={() => this.handleClick(i, j)}
      />
    );
  }
  render() {
    const winner = calculateWinner(this.state.squares);
    let status;
    if (winner) {
      if (winner === 'draw') {
        status = 'DRAW'
      } else {
        status = 'Winner: ' + winner
      }
    } else {
      status = 'Next player: ' + (this.state.isX ? 'X' : 'O');
    }

    return (
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.renderSquare(0, 0)}
          {this.renderSquare(0, 1)}
          {this.renderSquare(0, 2)}
          {this.renderSquare(0, 3)}
          {this.renderSquare(0, 4)}
          {this.renderSquare(0, 5)}
          {this.renderSquare(0, 6)}
          {this.renderSquare(0, 7)}
        </div>
        <div className="board-row">
          {this.renderSquare(1, 0)}
          {this.renderSquare(1, 1)}
          {this.renderSquare(1, 2)}
          {this.renderSquare(1, 3)}
          {this.renderSquare(1, 4)}
          {this.renderSquare(1, 5)}
          {this.renderSquare(1, 6)}
          {this.renderSquare(1, 7)}
        </div>
        <div className="board-row">
          {this.renderSquare(2, 0)}
          {this.renderSquare(2, 1)}
          {this.renderSquare(2, 2)}
          {this.renderSquare(2, 3)}
          {this.renderSquare(2, 4)}
          {this.renderSquare(2, 5)}
          {this.renderSquare(2, 6)}
          {this.renderSquare(2, 7)}
        </div>
        <div className="board-row">
          {this.renderSquare(3, 0)}
          {this.renderSquare(3, 1)}
          {this.renderSquare(3, 2)}
          {this.renderSquare(3, 3)}
          {this.renderSquare(3, 4)}
          {this.renderSquare(3, 5)}
          {this.renderSquare(3, 6)}
          {this.renderSquare(3, 7)}
        </div>
        <div className="board-row">
          {this.renderSquare(4, 0)}
          {this.renderSquare(4, 1)}
          {this.renderSquare(4, 2)}
          {this.renderSquare(4, 3)}
          {this.renderSquare(4, 4)}
          {this.renderSquare(4, 5)}
          {this.renderSquare(4, 6)}
          {this.renderSquare(4, 7)}
        </div>
        <div className="board-row">
          {this.renderSquare(5, 0)}
          {this.renderSquare(5, 1)}
          {this.renderSquare(5, 2)}
          {this.renderSquare(5, 3)}
          {this.renderSquare(5, 4)}
          {this.renderSquare(5, 5)}
          {this.renderSquare(5, 6)}
          {this.renderSquare(5, 7)}
        </div>
        <div className="board-row">
          {this.renderSquare(6, 0)}
          {this.renderSquare(6, 1)}
          {this.renderSquare(6, 2)}
          {this.renderSquare(6, 3)}
          {this.renderSquare(6, 4)}
          {this.renderSquare(6, 5)}
          {this.renderSquare(6, 6)}
          {this.renderSquare(6, 7)}
        </div>
        <div className="board-row">
          {this.renderSquare(7, 0)}
          {this.renderSquare(7, 1)}
          {this.renderSquare(7, 2)}
          {this.renderSquare(7, 3)}
          {this.renderSquare(7, 4)}
          {this.renderSquare(7, 5)}
          {this.renderSquare(7, 6)}
          {this.renderSquare(7, 7)}
        </div>
      </div>
    );
  }
}
class Game extends React.Component {
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div>{/*status*/}</div>
          <ol>{/*TODO */}</ol>
        </div>
      </div>
    )
  }
}
function alter(squares, i, j) {
  var value = squares[i][j];
  var dirs = makeDir();
  //tempの型はi,jの配列を保持する配列
  var temp = [];

  // dirLoop:
  // for(dirs8方向ぶん回す){
  //   //枠外はみ出すまでtemp配列回す＆追加
  while (true) {//dirぶんXY軸足す足す足す
    i++;
    if (i < 0 || i > 7 || j < 0 || j > 7) {//枠外に出たこのdir処理は強制停止
      console.log("枠外");
      break;//dirLoop;
    } else if (squares[i][j] == null) {//中身がnullならこのdir処理は強制停止
      console.log("null");
