  for (let s = 0; s < temp.length; s++) {//temp配列を順番にとって
    //temp配列の値をalters配列に入れる
    alter.push(temp[s]);
  }
  console.log(alter);
  // //alters配列が0ならおしまい(バリデーションできるとよき)
