import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Square(props) {
  return (
    <button
      className="square"
      onClick={() => props.onClick()}
    >
      {props.value}
    </button>
  );
}
class Board extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      squares: Array(64).fill(null),
      isX: true,
    };
  }
  handleClick(i) {
    const squares = this.state.squares.slice();
    if (calculateWinner(squares) || squares[i]) {
      return;
    }
    squares[i] = this.state.isX ? 'X' : 'O';
    this.setState({
      squares: squares,
      isX: !this.state.isX
    });
    console.log(squares);
  }
  renderSquare(i) {
    return (
      <Square
        value={this.state.squares[i]}
        onClick={() => this.handleClick(i)}
      />
    );
  }
  render() {
    const winner = calculateWinner(this.state.squares);
    let status;
    if (winner) {
      if (winner === 'draw') {
        status = 'DRAW'
      } else {
        status = 'Winner: ' + winner
      }
    } else {
      status = 'Next player: ' + (this.state.isX ? 'X' : 'O');
    }

    return (
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
          {this.renderSquare(6)}
          {this.renderSquare(7)}
        </div>
        <div className="board-row">
          {this.renderSquare(8)}
          {this.renderSquare(9)}
          {this.renderSquare(10)}
          {this.renderSquare(11)}
          {this.renderSquare(12)}
          {this.renderSquare(13)}
          {this.renderSquare(14)}
          {this.renderSquare(15)}
        </div>
        <div className="board-row">
          {this.renderSquare(16)}
          {this.renderSquare(17)}
          {this.renderSquare(18)}
          {this.renderSquare(19)}
          {this.renderSquare(20)}
          {this.renderSquare(21)}
          {this.renderSquare(22)}
          {this.renderSquare(23)}
        </div>
        <div className="board-row">
          {this.renderSquare(24)}
          {this.renderSquare(25)}
          {this.renderSquare(26)}
          {this.renderSquare(27)}
          {this.renderSquare(28)}
          {this.renderSquare(29)}
          {this.renderSquare(30)}
          {this.renderSquare(31)}
        </div>
        <div className="board-row">
          {this.renderSquare(32)}
          {this.renderSquare(33)}
          {this.renderSquare(34)}
          {this.renderSquare(35)}
          {this.renderSquare(36)}
          {this.renderSquare(37)}
          {this.renderSquare(38)}
          {this.renderSquare(39)}
        </div>
        <div className="board-row">
          {this.renderSquare(40)}
          {this.renderSquare(41)}
          {this.renderSquare(42)}
          {this.renderSquare(43)}
          {this.renderSquare(44)}
          {this.renderSquare(45)}
          {this.renderSquare(46)}
          {this.renderSquare(47)}
        </div>
        <div className="board-row">
          {this.renderSquare(48)}
          {this.renderSquare(49)}
          {this.renderSquare(50)}
          {this.renderSquare(51)}
          {this.renderSquare(52)}
          {this.renderSquare(53)}
          {this.renderSquare(54)}
          {this.renderSquare(55)}
        </div>
        <div className="board-row">
          {this.renderSquare(56)}
          {this.renderSquare(57)}
          {this.renderSquare(58)}
          {this.renderSquare(59)}
          {this.renderSquare(60)}
          {this.renderSquare(61)}
          {this.renderSquare(62)}
          {this.renderSquare(63)}
        </div>
      </div>
    );
  }
}
class Game extends React.Component {
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div>{/*status*/}</div>
          <ol>{/*TODO */}</ol>
        </div>
      </div>
    )
  }
}

function alter(squares, square) {
  var dirs = makeDir();
  for (let i = 0; i < dirs.length; i++) {
    var focusGrid = square + dirs;
    //枠外
    if (focusGrid > squares.length && focusGrid < 0) {
      break;
    }
    //TODO 周りに敵の石がない
    if (false) {
      break;
    }
    //TODO 進んで行って自分の石がない
    if (false) {
      break;
    }
    //隣に敵の石がある＆先に自分の石がある状態。
    //石をひっくり返す
    if (focusGrid === 'X') {
      focusGrid = 'O';
    } else {
      focusGrid = 'X';
    }
  }
}

function makeDir() {
  var dirX = [-1, 0, 1];
  var dirY = [-8, 0, 8];
  var dirArray = [];
  for (let x = 0; x < dirX.length; x++) {
    for (let y = 0; y < dirY.length; y++) {
      let i = dirX[x] + dirY[y];
      if (i === 0) {
        break;
      }
      dirArray.push(i);
    }
  }
  return dirArray;
}

function calculateWinner(squares) {
  let x = 0;
  let o = 0;
  for (let i = 0; i <= 63; i++) {
    if (squares[i] === null) {
      return null;
    }
    if (squares[i] === 'X') {
      x++;
      continue;
    }
    o++;
  }
  if (x > o) {
    return 'X';
  } else if (o > x) {
    return 'O';
  }
  return 'draw';
}

ReactDOM.render(
  <Game />,
  document.getElementById('root')
);
